import React, { Component } from "react";
import axios from "axios";

import Card from "../style/card/Card";
import "./Content.css";

import Data from "../../data/projects";

class Content extends Component {
  state = {
    users: [],
  };
  pegar = () => {
    axios.get("/api/users").then((e) => {
      const users = e.data.users;
      this.setState({
        users,
      });
    });
  };

  mostrar = () => {
    return (
      <div>
        {this.state.users.map((a) => {
          return (
            <Card key={a.id} id={a.id} name_author={a.name}/>
          );
        })}
      </div>
    );
  };

  render() {
    return (
      <div className="content">
        <h1>Projetos Data Science</h1>
        <p>
          Bem vindo a Data Science inc©. Sua plataforma para visualização de
          dados
        </p>
        {this.mostrar()}
        <button onClick={this.pegar}>Pegar</button>
        <hr color="red" />
        {Data.map((project) => {
          return (
            <Card
              key={project.id}
              id={project.id}
              name_author={project.name}
              name_project={project.project_name}
              content={project.content}
              github={project.github}
              describe={project.describe}
              lenguages={project.lenguages}
              img1={project.img1}
              img2={project.img2}
              type_img1={project.type_img1}
              type_img2={project.type_img2}
              img_description1={project.img_description1}
              img_description2={project.img_description2}
            />
          );
        })}
      </div>
    );
  }
}

export default Content;
