import React, { useState, Component } from "react";
import Axios from "axios";

import Card from "../style/card/Card";
import Projects from "../../data/projects";
import "./Content.css";

class Content extends Component {
  state = {
    users: {
      id: "",
      nome: "",
    },
    list: [],
  };

  pegar = () => {
    Axios.get("/api/users").then((res) => {
      const list = this.add(res.data);
      this.setState({
        list,
      });
    });
  };

  add(usuario) {
    const list = [];
    list.unshift(usuario);
    console.log(list.map(a => console.table(a.users)))
    return list;
  }

  card = () => {
    const projetos = Projects.map((project) => {
      return (
        <Card
          id={project.id}
          name_author={project.name}
          name_project={project.project_name}
          content={project.content}
          github={project.github}
          describe={project.describe}
          lenguages={project.lenguages}
          img1={project.img1}
          img2={project.img2}
          type_img1={project.type_img1}
          type_img2={project.type_img2}
          img_description1={project.img_description1}
          img_description2={project.img_description2}
        />
      );
    });
  };

  render() {
    return (
      <div className="content">
        <h1>Projetos Data Science</h1>
        <p>
          Bem vindo a Data Science inc©. Sua plataforma para visualização de
          dados
        </p>
        <button onClick={(e) => this.pegar(e)}>A</button>
        <hr color="red" />
        <p>
          {this.state.list.map((a) => {
            return <li key={a.users.id}>{a.users.nome}</li>;
          })}
        </p>
      </div>
    );
  }
}

export default Content;
